db:select.db:input-parameters:
#[{
	restaurantname: '%'++ vars.name as String default ''  ++ "%",
	limit: vars.pageSize as Number default  2,
	offset: (vars.page as Number -1)* vars.pageSize as Number default 0
}]

ee:message.ee:set-payload:
%dw 2.0
output application/json
---
payload map (item,index) ->{
	restaurantId: item.restaurant_id,
	name: item.name,
	address:{
		houseNumber: item.house_number,
		street: item.street,
		city:item.city,
		pincode: item.pincode
	},
	links:[
		{
			linkName: "menus",
			href: vars.requestUri as String ++ '/' ++ 
					item.restaurant_id as String++ "/menus"
		}
	]

}
